#include <iostream>
#include <unordered_map>

using namespace std;

class Solution {
public:
    bool checkInclusion(string s1, string s2) {
        int permutation[26];
        int s1_len = static_cast<int>(s1.size());
        
        for(int i=0;i<26;i++) {
            permutation[i] = 0;
        }
        
        for(char c1 : s1) {
            permutation[c1-'a']++;
        }
        
        int s2_len = static_cast<int>(s2.size());
        printf("s2_len %d\n", s2_len);
        for(int i=0;i<s2_len;i++) {
            int c2 = s2[i] - 'a';
            
            if(permutation[c2] != 0 && i + s1_len <= s2_len) {
                int copied[26];
                
                copy(permutation, permutation + 26, copied);
                
                int len = i + s1_len;
                
                bool isBreaked = false;
                
                for(int j=i;j<len;j++) {
                    c2 = s2[j] - 'a';
                    
                    if(copied[c2] > 0) {
                        copied[c2]--;
                    } else {
                        isBreaked = true;
                        break;
                    }
                }
                
                if(!isBreaked) {
                    isBreaked = false;
                    
                    for(int j=0;j<26;j++) {
                        if(copied[j] != 0) {
                            isBreaked = true;
                            
                            break;
                        }
                    }
                    
                    if (!isBreaked) {
                        return true;
                    }
                }
            }
        
        }
            
        return false;
    }
};

int main(int argc, const char * argv[]) {
    // insert code here...
    std::cout << Solution().checkInclusion("ab", "eidboaoo") << endl;
    std::cout << Solution().checkInclusion("ab", "eidba0oo") << endl;
    std::cout << Solution().checkInclusion("abc", "babc") << endl;
    std::cout << Solution().checkInclusion("","") << endl;
    return 0;
}
